CREATE TABLE TAG (TAGID SMALLINT NOT NULL, CREATEDATE DATE, NAME VARCHAR(255) UNIQUE, PRIMARY KEY (TAGID))
CREATE TABLE TASK (ID INTEGER NOT NULL, COMPLETED BOOLEAN, COMPLETEDDATE DATE, CREATEDDATE TIMESTAMP, DESCRIPTION VARCHAR(255), DUEDATE DATE, STARTDATE DATE, TITLE VARCHAR(255), UPDATEDDATE TIMESTAMP, TASKS_USERID INTEGER, PRIMARY KEY (ID))
CREATE TABLE USERMEMBER (USERID INTEGER NOT NULL, CREATEDATE DATE, UPDATEDATE DATE, USERNAME VARCHAR(255) UNIQUE, PRIMARY KEY (USERID))
CREATE TABLE TASK_TAG (Task_ID INTEGER NOT NULL, tags_TAGID SMALLINT NOT NULL, PRIMARY KEY (Task_ID, tags_TAGID))
ALTER TABLE TASK ADD CONSTRAINT FK_TASK_TASKS_USERID FOREIGN KEY (TASKS_USERID) REFERENCES USERMEMBER (USERID)
ALTER TABLE TASK_TAG ADD CONSTRAINT FK_TASK_TAG_tags_TAGID FOREIGN KEY (tags_TAGID) REFERENCES TAG (TAGID)
ALTER TABLE TASK_TAG ADD CONSTRAINT FK_TASK_TAG_Task_ID FOREIGN KEY (Task_ID) REFERENCES TASK (ID)
CREATE TABLE SEQUENCE (SEQ_NAME VARCHAR(50) NOT NULL, SEQ_COUNT DECIMAL(38), PRIMARY KEY (SEQ_NAME))
